import React, { useState } from 'react'

const Button = (props) => (
  <button onClick={props.handleClick}>
    {props.text}
  </button>
)

const Statistics = (props) => {
  return (
    <div>
      {props.text} {props.val}
    </div>
  )
}

const App = () => {
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const totalFeedback = good + neutral + bad
  const averageFeedback = (good - bad) / totalFeedback
  const pctPositiveFeedback = () => {
    return (
      good / totalFeedback + '%'
    )
  }

  return (
    <div>
      <h2>
        give feedback
      </h2>
      <Button handleClick={() => setGood(good + 1)} text='good' />
      <Button handleClick={() => setNeutral(neutral + 1)} text='neutral' />
      <Button handleClick={() => setBad(bad + 1)} text='bad' />

      <h2>
        statistics
      </h2>
      <Statistics text='good' val={good} />
      <Statistics text='neutral' val={neutral} />
      <Statistics text='bad' val={bad} />
      <Statistics text='all' val={totalFeedback} /> 
      <Statistics text='average' val={averageFeedback} />
      <Statistics text='positive' val={pctPositiveFeedback} />
    </div>
  )
}

export default App;
